import math

def is_prime(n):
    if n <= 1:
        return False
    if n <= 3:
        return True
    if n % 2 == 0 or n % 3 == 0:
        return False
    i = 5
    while i * i <= n:
        if n % i == 0 or n % (i + 2) == 0:
            return False
        i += 6
    return True

print(is_prime(91))

def product_of_consecutive_integers(n):
    return n * (n + 1)

max_sum = 0
max_pair = (0, 0)

for n in range(1, 400):
    if product_of_consecutive_integers(n) < 400:
        current_sum = n + (n + 1)
        if current_sum > max_sum:
            max_sum = current_sum
            max_pair = (n, n + 1)
    else:
        break

print(max_sum)

# Define the numbers on the spinner
numbers = [3, 6, 1, 4, 5, 2]

# Function to check if a number is prime
def is_prime(n):
    if n <= 1:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

# Identify prime numbers on the spinner
prime_numbers = [n for n in numbers if is_prime(n)]
print(prime_numbers)

